import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import socket from "../socket";

const ROWS = 6;
const COLS = 7;

const GameBoard = ({ setInGame }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { username, gameId, opponent, turn: initialTurn } = location.state;

  const [board, setBoard] = useState(
    Array.from({ length: ROWS }, () => Array(COLS).fill(null))
  );
  const [isMyTurn, setIsMyTurn] = useState(initialTurn === username);
  const [currentTurnPlayer, setCurrentTurnPlayer] = useState(initialTurn);

  const makeMove = (col) => {
    if (!isMyTurn) return;
    socket.emit("makeMove", { gameId, column: col });
  };

  useEffect(() => {
    const handleGameStarted = ({ board: newBoard, turn }) => {
      setBoard(newBoard);
      setCurrentTurnPlayer(turn);
      setIsMyTurn(turn === username);
    };

    const handleMoveMade = ({ board: newBoard, turn: nextTurn }) => {
      setBoard(newBoard);
      setCurrentTurnPlayer(nextTurn);
      setIsMyTurn(nextTurn === username);
    };

    const handleGameOver = ({ winner, draw }) => {
      setTimeout(() => {
        if (draw) {
          alert("ü§ù It's a draw!");
        } else if (winner === username) {
          alert("üèÜ You win!");
        } else {
          alert(`üòû ${winner} wins.`);
        }
        setInGame(false);
        navigate("/");
      }, 300);
    };

    socket.on("gameStarted", handleGameStarted);
    socket.on("moveMade", handleMoveMade);
    socket.on("gameOver", handleGameOver);

    return () => {
      socket.off("gameStarted", handleGameStarted);
      socket.off("moveMade", handleMoveMade);
      socket.off("gameOver", handleGameOver);
    };
  }, [username, gameId, navigate, setInGame]);

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#0b1d1f",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "40px 20px",
        color: "#ffffff",
      }}
    >
      <h1 style={{ fontSize: "32px", marginBottom: "10px" }}>Connect4</h1>
      <h3 style={{ fontSize: "18px", marginBottom: "5px" }}>
        Your Turn:{" "}
        <span style={{ color: "#6ee7b7", fontWeight: "bold" }}>
          {isMyTurn ? "‚úÖ Your Move" : "‚è≥ Waiting"}
        </span>
      </h3>
      <h4 style={{ fontSize: "16px", marginBottom: "20px" }}>
        Playing against: <span style={{ color: "#6ee7b7" }}>{opponent}</span>
      </h4>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${COLS}, 60px)`,
          gap: "6px",
          backgroundColor: "#122c2f",
          padding: "20px",
          borderRadius: "12px",
        }}
      >
        {board.map((row, rIdx) =>
          row.map((cell, cIdx) => (
            <div
              key={`${rIdx}-${cIdx}`}
              onClick={() => makeMove(cIdx)}
              style={{
                width: "60px",
                height: "60px",
                backgroundColor:
                  cell === "X"
                    ? "#d62828"
                    : cell === "O"
                    ? "#fcbf49"
                    : "#1e3d3f",
                borderRadius: "50%",
                cursor: isMyTurn ? "pointer" : "not-allowed",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                transition: "background 0.2s",
              }}
            >
              {cell && (
                <span
                  style={{
                    fontSize: "24px",
                    color: "#ffffff",
                    fontWeight: "bold",
                  }}
                >
                  {cell}
                </span>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default GameBoard;
