import React, { useEffect, useState } from "react";
import socket from "../socket";

const ROWS = 6;
const COLS = 7;

const GameBoard = ({ username, gameId, turnProp, opponentProp, setInGame }) => {
  const [board, setBoard] = useState(Array.from({ length: ROWS }, () => Array(COLS).fill(null)));
  const [turn, setTurn] = useState(turnProp || null);
  const [opponent, setOpponent] = useState(opponentProp || null);

  const makeMove = (col) => {
    if (turn !== username) return;
    socket.emit("makeMove", { gameId, column: col });
  };

  useEffect(() => {
    setTurn(turnProp);
    setOpponent(opponentProp);
  }, [turnProp, opponentProp]);

  useEffect(() => {
    console.log("ðŸ” GameBoard mounted. Username:", username, "Opponent:", opponent);

    socket.on("moveMade", ({ board, turn }) => {
      setBoard([...board]);
      if (turn) setTurn(turn);
    });

    socket.on("gameOver", ({ winner, draw }) => {
      if (draw) {
        alert("It's a Draw!");
      } else {
        alert(winner === username ? "You Win!" : `${winner} Wins`);
      }
      setInGame(false);
    });

    socket.on("playerDisconnected", ({ message }) => alert(message));
    socket.on("playerRejoined", ({ message }) => alert(message));

    return () => {
      socket.off("moveMade");
      socket.off("gameOver");
      socket.off("playerDisconnected");
      socket.off("playerRejoined");
    };
  }, [username, setInGame]);

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>4 in a Row</h2>
      <h3>Turn: {turn || "Loading..."}</h3>
      <h4>Opponent: {opponent || "Waiting..."}</h4>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${COLS}, 50px)`,
          justifyContent: "center",
          gap: "5px",
        }}
      >
        {board.map((row, rIdx) =>
          row.map((cell, cIdx) => (
            <div
              key={`${rIdx}-${cIdx}`}
              onClick={() => makeMove(cIdx)}
              style={{
                width: 50,
                height: 50,
                backgroundColor:
                  cell === "X" ? "red" : cell === "O" ? "yellow" : "#eee",
                border: "1px solid #333",
                borderRadius: "50%",
                cursor: turn === username ? "pointer" : "not-allowed",
              }}
            ></div>
          ))
        )}
      </div>
    </div>
  );
};

export default GameBoard;
